template.value:
$args = array (
	'post_type'				=> 'post_type',
	'post_status'			=> 'publish',
	'pagination'			=> true,
	'posts_per_page'		=> -1,
	'ignore_sticky_posts'	=> false,
	'order'					=> 'DESC',
	'orderby'				=> 'date',
	'meta_query'			=> array(
		array(
			'key'		=> 'custom_field_key',
			'value'		=> 'custom_field_value',
			'compare'	=> 'NOT IN', //LIKE, NOT LIKE, IN, BETWEEN, NOT BETWEEN, EXISTS
		),
	),
	'tax_query' => array(
		'relation' => 'AND',
		array(
			'taxonomy' => 'movie_genre',
			'field'    => 'slug',
			'terms'    => array( 'action', 'comedy' ),
		),
		array(
			'taxonomy' => 'actor',
			'field'    => 'term_id',
			'terms'    => array( 103, 115, 206 ),
			'operator' => 'NOT IN',
		),
	),
);

/** @var WP_Query $query */
$query = new WP_Query( $args );

if ( $query->have_posts() ) {
	while ( $query->have_posts() ) {
		$query->the_post();
		// do something
	}
}

wp_reset_postdata();

if( have_rows('repeater_field_name') ){

    while ( have_rows('repeater_field_name') ){ 
		the_row();

        the_sub_field('sub_field_name');

    }
}
